/* global AFRAME, NAF */

import React from 'react'
import dynamic from 'next/dynamic';

dynamic(
    // @ts-ignore
  () => {
        // @ts-ignore

    import('webrtc-adapter')
    import('aframe')
    // @ts-ignore
    import('networked-aframe')
        // @ts-ignore
    import('naf-janus-adapter/dist/naf-janus-adapter')
  },
  { ssr: false }
)


//Networking
export default class Scene extends React.Component {
onComponentLoaded(){
  const NetworkedScene = dynamic(() : any => {
    import('./scene-networked')
  }
  , {
    ssr: false
  });

  const LocalScene = dynamic(() : any => {

  }
  , {
    ssr: false
  });
}

  state = {
    loggedIn: false // TODO: Add auth and vuex store
  };

  isLoggedIn() {
    return this.state.loggedIn
  }

  constructor(props: any) {
    super(props); 
  }

  render() {
      if(this.isLoggedIn()) 
        return ( <this.NetworkedScene /> )
      else
        return ( <this.LocalScene /> )
  }
}
